/** * This class is simply a linked list taught within all computer science courses which is used throughout the Dictionary.java class to  * represent a list and to simply the overall class so it is not so conjested.  * @author Izum Adnan * */public class Node {    private Node next;    private Record element;  public Node(){	next = null;	element = null;}public Node (Record element){	next = null; 	element = element;} public Node getNext(){	return next;}public void setNext (Node node){	next = node;}    public Record getElement() {	return element;}public void setElement (Record element){	element = element;}}